package cn.dmego.leetcode.codetop.medium;

/**
 * @author dmego
 * @date 2022/01/05 09:23
 */
public class LC_5 {

    public String longestPalindrome(String s) {
        String longest = "";
        int maxLen = 0;
        int i = 0, j = 0;
        while (i < s.length()) {
            if (j < s.length()) {
                if (palindrome(s.substring(i, j + 1))) {
                    int len = j - i + 1;
                    if (len > maxLen) {
                        maxLen = len;
                        longest = s.substring(i, j + 1);
                    }
                }
                j++;
            } else {
                i++;
                j = i;
            }
        }
        return longest;
    }

    // 判断一个字符串是否是回文
    public boolean palindrome(String s) {
        int i = 0, j = s.length() - 1;
        while (i <= j) {
            if (s.charAt(i) != s.charAt(j)) {
                return false;
            }
            i++;
            j--;
        }
        return true;
    }

    public static void main(String[] args) {
        String s = "";
        LC_5 lc = new LC_5();
        String s1 = lc.longestPalindrome(s);
        System.out.println(s1);
    }

}
